            --accent: #00ff9d;
            --secondary: #bd00ff;
            --tertiary: #00d5ff;
            --danger: #ff0055;
            --nav-height: 70px;
        }

        * {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
        }

        body {
            background-color: var(--bg);
            color: var(--text);
            font-family: 'Space Mono', monospace;
            overflow: hidden;
            height: 100vh;
            display: flex;
            flex-direction: column;
        }

        /* --- CRT EFFECT --- */
        .crt::before {
            content: " ";
            display: block;
            position: absolute;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;
            background: linear-gradient(rgba(18, 16, 16, 0) 50%, rgba(0, 0, 0, 0.25) 50%), linear-gradient(90deg, rgba(255, 0, 0, 0.06), rgba(0, 255, 0, 0.02), rgba(0, 0, 255, 0.06));
            z-index: 999;
            background-size: 100% 2px, 3px 100%;
            pointer-events: none;
        }

        .crt::after {
            content: " ";
            display: block;
            position: absolute;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;
            background: rgba(18, 16, 16, 0.1);
            opacity: 0;
            z-index: 999;
            pointer-events: none;
            animation: flicker 0.15s infinite;
        }

        @keyframes flicker {
            0% {
                opacity: 0.01;
            }

            50% {
                opacity: 0.03;
            }

            100% {
                opacity: 0.01;
            }
        }

        /* --- NAV --- */
        nav {
            height: var(--nav-height);
            display: flex;
            justify-content: center;
            align-items: center;
            border-bottom: 1px solid rgba(255, 255, 255, 0.1);
            background: rgba(2, 2, 2, 0.95);
            backdrop-filter: blur(10px);
            z-index: 1000;
            position: relative;
        }

        .nav-link {
            background: transparent;
            border: 1px solid transparent;
            color: #555;
            padding: 0.6rem 1.2rem;
            margin: 0 0.5rem;
            cursor: pointer;
            font-family: inherit;
            font-size: 0.8rem;
            text-transform: uppercase;
            letter-spacing: 2px;
            transition: all 0.2s ease;
            position: relative;
            overflow: hidden;
        }

        .nav-link::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
            transition: 0.5s;
        }

        .nav-link:hover::before {
            left: 100%;
        }

        .nav-link:hover {
            color: var(--text);
            border-color: #333;
        }

        .nav-link.active {
            color: var(--bg);
            background: var(--accent);
            border-color: var(--accent);
            box-shadow: 0 0 15px var(--accent);
            font-weight: bold;
        }

        .nav-link.active:nth-child(2) {
            background: var(--tertiary);
            box-shadow: 0 0 15px var(--tertiary);
            border-color: var(--tertiary);
        }

        .nav-link.active:nth-child(3) {
            background: var(--secondary);
            box-shadow: 0 0 15px var(--secondary);
            border-color: var(--secondary);
        }

        .nav-link.active:nth-child(4) {
            background: var(--danger);
            box-shadow: 0 0 15px var(--danger);
            border-color: var(--danger);
        }

        /* --- MAIN --- */
        main {
            flex: 1;
            position: relative;
            width: 100%;
        }

        .gallery {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            opacity: 0;
            pointer-events: none;
            transition: opacity 0.8s cubic-bezier(0.16, 1, 0.3, 1);
            /* Zoom effect on transition */
            transform: scale(0.95);
        }

        .gallery.active {
            opacity: 1;
            pointer-events: all;
            transform: scale(1);
        }

        .gallery-label {
            position: absolute;
            bottom: 30px;
            left: 30px;
            font-size: 0.7rem;
            letter-spacing: 2px;
            color: rgba(255, 255, 255, 0.4);
            border-left: 2px solid currentColor;
            padding-left: 10px;
        }

        .stats {
            position: absolute;
            top: 10px;
            right: 10px;
            font-size: 0.7rem;
            color: rgba(255, 255, 255, 0.2);
            text-align: right;
            line-height: 1.4;
        }

        canvas,
