            width: 100%;
            height: 100%;
            display: block;
        }

        /* AUDIO BTN */
        #audio-toggle {
            position: absolute;
            bottom: 30px;
            right: 30px;
            background: transparent;
            border: 1px solid rgba(255, 255, 255, 0.2);
            color: rgba(255, 255, 255, 0.5);
            padding: 5px 10px;
            cursor: pointer;
            font-family: inherit;
            font-size: 0.7rem;
            z-index: 2000;
        }

        #audio-toggle:hover {
            border-color: var(--accent);
            color: var(--accent);
        }
    </style>
</head>

<body class="crt">

    <nav>
        <button class="nav-link active" data-target="gallery-fractal">I. RECURSION</button>
        <button class="nav-link" data-target="gallery-chaos">II. SWARM</button>
        <button class="nav-link" data-target="gallery-sacred">III. METATRON</button>
        <button class="nav-link" data-target="gallery-hyper">IV. TESSERACT</button>
    </nav>

    <button id="audio-toggle">AUDIO: OFF</button>

    <main>
        <!-- I. FRACTAL -->
        <section id="gallery-fractal" class="gallery active">
            <svg id="fractal-svg" xmlns="http://www.w3.org/2000/svg"
                style="filter: drop-shadow(0 0 5px rgba(0,255,157,0.5));"></svg>
            <div class="gallery-label">
                SYSTEM: PYTHAGOREAN_L-SYSTEM<br>
                STATUS: ANIMATED<br>
                Render: SVG VECTOR
            </div>
            <div class="stats" id="fractal-stats">DEPTH: 10<br>NODES: 2048</div>
        </section>

        <!-- II. BOIDS (CHAOS) -->
        <section id="gallery-chaos" class="gallery">
            <canvas id="chaos-canvas"></canvas>
            <div class="gallery-label">
                SYSTEM: FLOCKING_SIM<br>
                ALGO: BOIDS (Reynolds)<br>
                Render: CANVAS 2D
            </div>
            <div class="stats" id="chaos-stats">ENTITIES: 200<br>FPS: 60</div>
        </section>

        <!-- III. SACRED -->
        <section id="gallery-sacred" class="gallery">
            <svg id="sacred-svg" xmlns="http://www.w3.org/2000/svg"
                style="filter: drop-shadow(0 0 8px rgba(189,0,255,0.4));"></svg>
            <div class="gallery-label">
                SYSTEM: SACRED_GEOMETRY<br>
                FORM: METATRONS_CUBE<br>
                Render: SVG VECTOR
            </div>
        </section>

        <!-- IV. HYPERCUBE -->
        <section id="gallery-hyper" class="gallery">
            <canvas id="hyper-canvas"></canvas>
            <div class="gallery-label">
                SYSTEM: 4D_PROJECTION<br>
                OBJECT: TESSERACT (Hypercube)<br>
                Render: MATRIX_MATH
            </div>
            <div class="stats">DIMENSIONS: 4<br>VERTICES: 16</div>
        </section>
    </main>

    <script>
        // --- CORE ---
        const width = () => window.innerWidth;
        const height = () => window.innerHeight - 70;

        let activeGallery = 'gallery-fractal';

        // --- AUDIO ENGINE (Generative) ---
        const AudioEngine = {
            ctx: null,
            isPlaying: false,
            oscs: [],
            init() {
                const AudioContext = window.AudioContext || window.webkitAudioContext;
                this.ctx = new AudioContext();
                this.master = this.ctx.createGain();
                this.master.gain.value = 0.1;
                this.master.connect(this.ctx.destination);
            },
            toggle() {
                if (!this.ctx) this.init();
                if (this.ctx.state === 'suspended') this.ctx.resume();

                this.isPlaying = !this.isPlaying;
                const btn = document.getElementById('audio-toggle');
                btn.textContent = this.isPlaying ? "AUDIO: ACTIVE" : "AUDIO: OFF";

                if (this.isPlaying) this.startDrone();
                else this.stopDrone();
            },
            startDrone() {
                // Create a chord
                const freqs = [110, 164.81, 196, 220]; // A2, E3, G3, A3 (Am7)
                freqs.forEach(f => {
                    const osc = this.ctx.createOscillator();
                    osc.type = 'sine';
                    osc.frequency.value = f;

                    const gain = this.ctx.createGain();
                    gain.gain.value = 0;

                    osc.connect(gain);
                    gain.connect(this.master);
                    osc.start();

                    // Fade in
                    gain.gain.setTargetAtTime(0.1, this.ctx.currentTime, 2);

                    this.oscs.push({ node: osc, gain: gain });
                });
            },
            stopDrone() {
                this.oscs.forEach(o => {
                    o.gain.gain.setTargetAtTime(0, this.ctx.currentTime, 0.5);
                    setTimeout(() => o.node.stop(), 600);
                });
                this.oscs = [];
            }
        };

        document.getElementById('audio-toggle').addEventListener('click', () => AudioEngine.toggle());

        // --- NAV ---
        document.querySelectorAll('.nav-link').forEach(link => {
            link.addEventListener('click', () => {
                document.querySelectorAll('.nav-link').forEach(l => l.classList.remove('active'));
                link.classList.add('active');

                document.querySelectorAll('.gallery').forEach(g => g.classList.remove('active'));
                const tid = link.dataset.target;
                document.getElementById(tid).classList.add('active');

                activeGallery = tid;
                if (tid === 'gallery-chaos') resizeChaos();
                if (tid === 'gallery-hyper') resizeHyper();
            });
        });

        // --- 1. FRACTAL TREE (L-System Style) ---
        const fractalSvg = document.getElementById('fractal-svg');
        let fTime = 0;

        function drawRecursiveLine(x, y, len, angle, depth) {
            if (depth === 0) return "";

            const x2 = x + len * Math.cos(angle);
            const y2 = y + len * Math.sin(angle);

            let d = `M${x.toFixed(1)},${y.toFixed(1)} L${x2.toFixed(1)},${y2.toFixed(1)} `;

            // Wind Simulation
            const wind = Math.sin(fTime * 0.001 + depth * 0.5) * 0.2;
            const split = 0.5 + Math.sin(fTime * 0.0005) * 0.1;

            d += drawRecursiveLine(x2, y2, len * 0.7, angle - split + wind, depth - 1);
            d += drawRecursiveLine(x2, y2, len * 0.7, angle + split + wind, depth - 1);

            return d;
        }

        function renderFractal() {
            if (activeGallery === 'gallery-fractal') {
                fTime += 16;
                const path = drawRecursiveLine(width() / 2, height(), height() * 0.28, -Math.PI / 2, 9);

                let el = fractalSvg.querySelector('path');
                if (!el) {
                    el = document.createElementNS("http://www.w3.org/2000/svg", 'path');
                    el.setAttribute('stroke', '#00ff9d');
                    el.setAttribute('stroke-width', '1.5');
                    el.setAttribute('fill', 'none');
                    el.setAttribute('stroke-linecap', 'round'); // High end feel
                    fractalSvg.appendChild(el);
                }
                el.setAttribute('d', path);
            }
            requestAnimationFrame(renderFractal);
        }
        renderFractal();

        // --- 2. BOIDS (Complex Flocking) ---
        const chaosCanvas = document.getElementById('chaos-canvas');
        const cx = chaosCanvas.getContext('2d');
        let boids = [];

        function resizeChaos() { chaosCanvas.width = width(); chaosCanvas.height = height(); }
        window.addEventListener('resize', resizeChaos); resizeChaos();

        class Boid {
            constructor() {
                this.pos = { x: Math.random() * width(), y: Math.random() * height() };
                this.vel = { x: Math.random() * 4 - 2, y: Math.random() * 4 - 2 };
                this.acc = { x: 0, y: 0 };
                this.maxSpeed = 4;
                this.maxForce = 0.1;
            }

            update() {
                this.vel.x += this.acc.x; this.vel.y += this.acc.y;
                // Limit speed
                const speed = Math.hypot(this.vel.x, this.vel.y);
                if (speed > this.maxSpeed) {
                    this.vel.x = (this.vel.x / speed) * this.maxSpeed;
                    this.vel.y = (this.vel.y / speed) * this.maxSpeed;
                }
                this.pos.x += this.vel.x; this.pos.y += this.vel.y;
                this.acc = { x: 0, y: 0 };

                // Wrap
                if (this.pos.x < 0) this.pos.x = width();
                if (this.pos.x > width()) this.pos.x = 0;
                if (this.pos.y < 0) this.pos.y = height();
                if (this.pos.y > height()) this.pos.y = 0;
            }

            draw() {
                const angle = Math.atan2(this.vel.y, this.vel.x);
                cx.save();
                cx.translate(this.pos.x, this.pos.y);
                cx.rotate(angle);
                cx.beginPath();
                cx.moveTo(6, 0); cx.lineTo(-4, 3); cx.lineTo(-4, -3);
                cx.fillStyle = '#00d5ff';
                cx.fill();
                cx.restore();
            }

            flock(boids) {
                // Alignment, Cohesion, Separation
                let align = { x: 0, y: 0 }, cohesion = { x: 0, y: 0 }, separ = { x: 0, y: 0 };
                let total = 0;
                const perception = 50;

                for (let other of boids) {
                    const d = Math.hypot(this.pos.x - other.pos.x, this.pos.y - other.pos.y);
                    if (other !== this && d < perception) {
                        align.x += other.vel.x; align.y += other.vel.y;
                        cohesion.x += other.pos.x; cohesion.y += other.pos.y;
                        separ.x += (this.pos.x - other.pos.x) / d; separ.y += (this.pos.y - other.pos.y) / d;
                        total++;
                    }
                }

                if (total > 0) {
                    align.x /= total; align.y /= total;
                    align.x = (align.x / Math.hypot(align.x, align.y)) * this.maxSpeed - this.vel.x;
                    align.y = (align.y / Math.hypot(align.x, align.y)) * this.maxSpeed - this.vel.y;

                    cohesion.x /= total; cohesion.y /= total;
                    cohesion.x = ((cohesion.x - this.pos.x) / 100) - this.vel.x; // Seek
                    cohesion.y = ((cohesion.y - this.pos.y) / 100) - this.vel.y;

                    separ.x *= 1.5; separ.y *= 1.5; // Strong separation
                }
                // Add forces
                this.acc.x += align.x * 0.1 + cohesion.x * 0.05 + separ.x * 0.2;
                this.acc.y += align.y * 0.1 + cohesion.y * 0.05 + separ.y * 0.2;
            }
        }

        for (let i = 0; i < 150; i++) boids.push(new Boid());

        function renderChaos() {
            if (activeGallery === 'gallery-chaos') {
                cx.fillStyle = 'rgba(2,2,2,0.2)'; // Trails
                cx.fillRect(0, 0, width(), height());

                // Draw connections
                cx.beginPath();
                for (let i = 0; i < boids.length; i++) {
                    boids[i].flock(boids);
                    boids[i].update();
                    boids[i].draw();

                    // Connect nearest
                    for (let j = i + 1; j < boids.length; j++) {
                        const d = Math.hypot(boids[i].pos.x - boids[j].pos.x, boids[i].pos.y - boids[j].pos.y);
                        if (d < 40) {
                            cx.moveTo(boids[i].pos.x, boids[i].pos.y);
                            cx.lineTo(boids[j].pos.x, boids[j].pos.y);
                        }
                    }
                }
                cx.strokeStyle = 'rgba(0, 213, 255, 0.1)';
                cx.stroke();
            }
            requestAnimationFrame(renderChaos);
        }
        renderChaos();


        // --- 3. SACRED GEOMETRY (Metatron) ---
        // Reuse similar logic but with group rotation and pulse
        const sacredSvg = document.getElementById('sacred-svg');
        const sacredGroup = document.createElementNS("http://www.w3.org/2000/svg", "g");
        sacredSvg.appendChild(sacredGroup);

        let sTime = 0;
        // Build Geometry Once
        const circles = [];
        const lines = [];
        const R = 70;

        const centers = [{ x: 0, y: 0 }];
        for (let k = 0; k < 6; k++) centers.push({ x: Math.cos(k * Math.PI / 3) * R * 2, y: Math.sin(k * Math.PI / 3) * R * 2 });
        for (let k = 0; k < 6; k++) centers.push({ x: Math.cos(k * Math.PI / 3) * R * 4, y: Math.sin(k * Math.PI / 3) * R * 4 });

        // Lines
        centers.forEach((c1, i) => {
            centers.slice(i + 1).forEach(c2 => {
                const l = document.createElementNS("http://www.w3.org/2000/svg", 'line');
                l.setAttribute('x1', c1.x); l.setAttribute('y1', c1.y);
                l.setAttribute('x2', c2.x); l.setAttribute('y2', c2.y);
                l.setAttribute('stroke', '#bd00ff');
                l.setAttribute('opacity', '0.2');
                sacredGroup.appendChild(l);
                lines.push(l);
            });
        });

        // Circles
        centers.forEach(c => {
            const circ = document.createElementNS("http://www.w3.org/2000/svg", 'circle');
            circ.setAttribute('cx', c.x); circ.setAttribute('cy', c.y); circ.setAttribute('r', R);
            circ.setAttribute('fill', 'none'); circ.setAttribute('stroke', '#fff');
            sacredGroup.appendChild(circ);
            circles.push(circ);
        });

        function renderSacred() {
            if (activeGallery === 'gallery-sacred') {
                sTime += 0.005;
                sacredGroup.setAttribute('transform', `translate(${width() / 2}, ${height() / 2}) rotate(${sTime * 20})`);

                circles.forEach((c, i) => {
                    const pulse = 1 + Math.sin(sTime * 5 + i) * 0.1;
                    c.setAttribute('transform', `matrix(${pulse},0,0,${pulse}, ${centers[i].x * (1 - pulse)}, ${centers[i].y * (1 - pulse)})`);
                    c.setAttribute('stroke', `hsl(${(sTime * 100 + i * 20) % 360}, 80%, 60%)`);
                });
            }
            requestAnimationFrame(renderSacred);
        }
        renderSacred();


        // --- 4. TESSERACT (4D MATH) ---
        const hyperCanvas = document.getElementById('hyper-canvas');
        const hx = hyperCanvas.getContext('2d');
        function resizeHyper() { hyperCanvas.width = width(); hyperCanvas.height = height(); }
        window.addEventListener('resize', resizeHyper); resizeHyper();

        let angle = 0;

        // 4D Point
        class P4 { constructor(x, y, z, w) { this.x = x; this.y = y; this.z = z; this.w = w; } }

        // Generate Tesseract Vertices (+-1, +-1, +-1, +-1)
        let points = [];
        for (let i = 0; i < 16; i++) {
            let x = (i & 1) ? 1 : -1;
            let y = (i & 2) ? 1 : -1;
            let z = (i & 4) ? 1 : -1;
            let w = (i & 8) ? 1 : -1;
            points.push(new P4(x, y, z, w));
        }

        // Connect everything that differs by 1 coord
        // That's tricky to map manually, let's just find neighbors.
        // Two points are connected if distance^2 == 4 (since 1 - -1 = 2, sq=4)
        // Hmm, simpler: iterate all pairs, if Hamming distance (coord diff) is 1, connect.

        function matMul(v, m) {
            // General Matrix mult logic
            // But we need Project 4D to 3D here
            // We use simple rotation matrices
        }

        function renderHyper() {
            if (activeGallery === 'gallery-hyper') {
                // Clear
                hx.fillStyle = '#020202';
                hx.fillRect(0, 0, width(), height());

                hx.strokeStyle = '#ff0055';
                hx.lineWidth = 2;

                angle += 0.01;

                // Rotation Matrices 4D
                // Just use XY and ZW rotations for cool double-rotation effect
                const s = Math.sin(angle);
                const c = Math.cos(angle);

                // Projected 3D points
                let projected3d = [];

                points.forEach(p => {
                    // Rotate ZW plane
                    // x, y, z*c - w*s, z*s + w*c
                    let z = p.z * c - p.w * s;
                    let w = p.z * s + p.w * c;
                    let x = p.x;
                    let y = p.y;

                    // Rotate XY plane
                    let x_new = x * c - y * s;
                    let y_new = x * s + y * c;
                    x = x_new;
                    y = y_new;

                    // Projection 4D -> 3D
                    // w-distance perspective
                    let distance = 3;
                    let wInv = 1 / (distance - w);

                    let p3 = {
                        x: x * wInv,
                        y: y * wInv,
                        z: z * wInv
                    };

                    // Projection 3D -> 2D
                    // z-distance
                    let zInv = 1 / (3 - p3.z);
                    projected3d.push({
                        x: p3.x * zInv * 300 + width() / 2,
                        y: p3.y * zInv * 300 + height() / 2
                    });
                });

                // Draw Edges
                // We know indices. Check if connected.
                hx.beginPath();
                for (let i = 0; i < 16; i++) {
                    for (let j = i + 1; j < 16; j++) {
                        // Check connectivity: only 1 coordinate different
                        let diff = 0;
                        if (points[i].x !== points[j].x) diff++;
                        if (points[i].y !== points[j].y) diff++;
                        if (points[i].z !== points[j].z) diff++;
                        if (points[i].w !== points[j].w) diff++;

                        if (diff === 1) {
                            hx.moveTo(projected3d[i].x, projected3d[i].y);
                            hx.lineTo(projected3d[j].x, projected3d[j].y);
                        }
                    }
                }
                hx.stroke();

                // Draw Nodes
                hx.fillStyle = '#fff';
                projected3d.forEach(p => {
                    hx.beginPath();
                    hx.arc(p.x, p.y, 4, 0, Math.PI * 2);
                    hx.fill();
                });
            }
            requestAnimationFrame(renderHyper);
        }
        renderHyper();

    </script>
