        const hypercubeEngine = { canvas: null, ctx: null, vertices: [], edges: [], angles: { xy: 0, xz: 0, xw: 0, yz: 0, yw: 0, zw: 0 }, paused: true, projection: 'perspective', init() { this.canvas = document.getElementById('hypercubeCanvas'); this.ctx = this.canvas.getContext('2d'); this.resize(); window.addEventListener('resize', () => this.resize()); this.generateHypercube() }, resize() { const r = this.canvas.parentElement.getBoundingClientRect(); this.canvas.width = r.width; this.canvas.height = r.height }, generateHypercube() { this.vertices = []; for (let i = 0; i < 16; i++) { this.vertices.push([i & 1 ? 1 : -1, i & 2 ? 1 : -1, i & 4 ? 1 : -1, i & 8 ? 1 : -1]) } this.edges = []; for (let i = 0; i < 16; i++)for (let j = i + 1; j < 16; j++) { let diff = 0; for (let k = 0; k < 4; k++)if (this.vertices[i][k] !== this.vertices[j][k]) diff++; if (diff === 1) this.edges.push([i, j]) } }, rotate4D(v, angles) { let [x, y, z, w] = v; let cos, sin, t; cos = Math.cos(angles.xy); sin = Math.sin(angles.xy); t = x * cos - y * sin; y = x * sin + y * cos; x = t; cos = Math.cos(angles.xz); sin = Math.sin(angles.xz); t = x * cos - z * sin; z = x * sin + z * cos; x = t; cos = Math.cos(angles.xw); sin = Math.sin(angles.xw); t = x * cos - w * sin; w = x * sin + w * cos; x = t; cos = Math.cos(angles.yz); sin = Math.sin(angles.yz); t = y * cos - z * sin; z = y * sin + z * cos; y = t; cos = Math.cos(angles.yw); sin = Math.sin(angles.yw); t = y * cos - w * sin; w = y * sin + w * cos; y = t; cos = Math.cos(angles.zw); sin = Math.sin(angles.zw); t = z * cos - w * sin; w = z * sin + w * cos; z = t; return [x, y, z, w] }, project(v) { const d = 3; const w1 = 1 / (d - v[3]); const scale = Math.min(this.canvas.width, this.canvas.height) * 0.15; return { x: this.canvas.width / 2 + v[0] * w1 * scale, y: this.canvas.height / 2 + v[1] * w1 * scale, z: v[2] * w1, w: v[3] } }, animate() { if (this.paused) return; this.ctx.fillStyle = 'rgba(10,10,15,0.15)'; this.ctx.fillRect(0, 0, this.canvas.wi