    <script>
        // Navigation
        const navButtons = document.querySelectorAll('.nav-btn');
        const galleries = document.querySelectorAll('.gallery');

        navButtons.forEach(btn => {
            btn.addEventListener('click', () => {
                const page = btn.dataset.page;

                // Update nav buttons
                navButtons.forEach(b => b.classList.remove('active'));
                btn.classList.add('active');

                // Update galleries
                galleries.forEach(gallery => {
                    gallery.classList.remove('active');
                    if (gallery.id === page) {
                        gallery.classList.add('active');
                        // Trigger resize to ensure proper sizing
                        setTimeout(() => {
                            if (page === 'fractals') {
                                resizeFractal();
                                animateFractal();
                            } else if (page === 'physics') {
                                resizePhysics();
                                animatePhysics();
                            } else if (page === 'geometry') {
                                resizeGeometry();
                                animateGeometry();
                            }
                        }, 100);
                    }
                });
            });
        });

        // Gallery 1: Fractal Tree
        const fractalSVG = document.getElementById('fractalSVG');
        const fractalContainer = document.getElementById('fractalContainer');
        const fractalStatus = document.getElementById('fractalStatus');
        const toggleWindBtn = document.getElementById('toggleWind');
        const toggleGrowthBtn = document.getElementById('toggleGrowth');
        const regenerateBtn = document.getElementById('regenerate');

        let windEnabled = true;
        let growthEnabled = true;
        let fractalDepth = 11;
        let branchCount = 0;
        let windAngle = 0;
        let growthFactor = 1;

        function resizeFractal() {
            const rect = fractalContainer.getBoundingClientRect();
            fractalSVG.setAttribute('viewBox', `0 0 ${rect.width} ${rect.height}`);
        }

        function clearFractal() {
            while (fractalSVG.firstChild) {
                fractalSVG.removeChild(fractalSVG.firstChild);
            }
            branchCount = 0;
        }

        function drawFractalTree(x1, y1, angle, depth, length, width) {
            if (depth === 0) return;

            // Add enhanced wind sway with multiple frequencies
            const sway = windEnabled ?
                Math.sin(windAngle + depth * 0.7) * 0.4 +
                Math.cos(windAngle * 1.5 + depth * 0.3) * 0.2 : 0;
            const currentAngle = angle + sway;

            // Calculate end point
            const x2 = x1 + Math.cos(currentAngle) * length * growthFactor;
            const y2 = y1 + Math.sin(currentAngle) * length * growthFactor;

            // Create branch with enhanced styling
            const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
            line.setAttribute('x1', x1);
            line.setAttribute('y1', y1);
            line.setAttribute('x2', x2);
            line.setAttribute('y2', y2);

            // Enhanced color gradient based on depth and time
            const time = Date.now() * 0.001;
            const hue = ((depth / fractalDepth) * 120 + 60 + Math.sin(time + depth) * 20) % 360;
            const saturation = 70 + Math.sin(time * 2 + depth) * 10;
            const lightness = 40 + depth * 3 + Math.cos(time * 1.5) * 5;
            const strokeColor = `hsl(${hue}, ${saturation}%, ${lightness}%)`;
            line.setAttribute('stroke', strokeColor);
            line.setAttribute('stroke-width', Math.max(1, width));
            line.setAttribute('stroke-linecap', 'round');
            line.setAttribute('stroke-opacity', 0.9);

            fractalSVG.appendChild(line);
            branchCount++;

            // Recursive branches with enhanced branching
            const newLength = length * (0.65 + Math.sin(time + depth) * 0.05);
            const newWidth = width * 0.72;
            const branchAngle = 0.4 + Math.sin(time * 2 + depth) * 0.1;

            // Left branch
            drawFractalTree(x2, y2, angle - branchAngle, depth - 1, newLength, newWidth);
            // Right branch
            drawFractalTree(x2, y2, angle + branchAngle, depth - 1, newLength, newWidth);

            // Optional third branch for more complexity
            if (depth > fractalDepth * 0.3 && Math.random() < 0.3) {
                drawFractalTree(x2, y2, angle + Math.sin(time + depth) * 0.2, depth - 1, newLength * 0.8, newWidth * 0.8);
            }
        }

        function animateFractal() {
            clearFractal();

            const rect = fractalContainer.getBoundingClientRect();
            const width = rect.width;
            const height = rect.height;

            // Base parameters with enhanced scaling
            const startX = width / 2;
            const startY = height - 60;
            const baseAngle = -Math.PI / 2; // Upwards
            const baseLength = Math.min(width, height) * 0.28;
            const trunkWidth = 10;

            // Draw the tree with enhanced parameters
            drawFractalTree(startX, startY, baseAngle, fractalDepth, baseLength, trunkWidth);

            // Update status with animated numbers
            fractalStatus.textContent = `Depth: ${fractalDepth} | Branches: ${branchCount}`;

            // Update animation parameters with enhanced effects
            if (windEnabled) {
                windAngle += 0.025;
            }

            if (growthEnabled) {
                growthFactor = 0.92 + Math.sin(Date.now() * 0.0012) * 0.08;
            }
        }

        // Gallery 2: Physics Particles
        const physicsCanvas = document.getElementById('physicsCanvas');
        const physicsContainer = document.getElementById('physicsContainer');
        const physicsStatus = document.getElementById('physicsStatus');
        const toggleAttractionBtn = document.getElementById('toggleAttraction');
        const toggleParticlesBtn = document.getElementById('toggleParticles');
        const resetParticlesBtn = document.getElementById('resetParticles');

        let attractionMode = true;
        let particlesEnabled = true;
        let particles = [];
        let mouse = { x: 0, y: 0, radius: 120 };
        let lastTime = 0;
        let fps = 60;

        class Particle {
            constructor(x, y) {
                this.x = x;
                this.y = y;
                this.vx = (Math.random() - 0.5) * 3;
                this.vy = (Math.random() - 0.5) * 3;
                this.size = Math.random() * 2.5 + 0.5;
                this.color = `hsl(${Math.random() * 360}, 80%, 65%)`;
                this.life = 1;
                this.trail = [];
                this.maxTrail = 5;
            }

            update(width, height) {
                // Store trail positions
                this.trail.push({ x: this.x, y: this.y });
                if (this.trail.length > this.maxTrail) {
                    this.trail.shift();
                }

                // Apply mouse interaction with enhanced physics
                const dx = mouse.x - this.x;
                const dy = mouse.y - this.y;
                const distance = Math.sqrt(dx * dx + dy * dy);

                if (distance < mouse.radius) {
                    const force = Math.pow((mouse.radius - distance) / mouse.radius, 2);
                    const angle = Math.atan2(dy, dx);
                    const strength = attractionMode ? 0.35 : -0.7;

                    this.vx += Math.cos(angle) * strength * force;
                    this.vy += Math.sin(angle) * strength * force;
                }

                // Apply enhanced physics
                this.vx *= 0.96; // Reduced friction for longer trails
                this.vy *= 0.96;
                this.vy += 0.01; // Gentle gravity

                this.x += this.vx;
                this.y += this.vy;

                // Boundary collision with bounce
                if (this.x < 0 || this.x > width) {
                    this.vx *= -0.9;
                    this.x = Math.max(0, Math.min(width, this.x));
                }
                if (this.y < 0 || this.y > height) {
                    this.vy *= -0.9;
                    this.y = Math.max(0, Math.min(height, this.y));
                }

                // Fade out over time
                this.life -= 0.0005;
                if (this.life <= 0) {
                    this.x = Math.random() * width;
                    this.y = Math.random() * height;
                    this.life = 1;
                    this.trail = [];
                }
            }

            draw(ctx) {
                // Draw trail
                if (this.trail.length > 1) {
                    ctx.beginPath();
                    ctx.moveTo(this.trail[0].x, this.trail[0].y);
                    for (let i = 1; i < this.trail.length; i++) {
                        ctx.lineTo(this.trail[i].x, this.trail[i].y);
                    }
                    ctx.strokeStyle = this.color;
                    ctx.globalAlpha = this.life * 0.3;
                    ctx.lineWidth = this.size * 0.5;
                    ctx.stroke();
                    ctx.globalAlpha = 1;
                }

                // Draw particle
                ctx.beginPath();
                ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                ctx.fillStyle = this.color;
                ctx.globalAlpha = this.life;
                ctx.fill();
                ctx.globalAlpha = 1;
            }
        }

        function resizePhysics() {
            const rect = physicsContainer.getBoundingClientRect();
            // Use device pixel ratio for crisp rendering
            const dpr = window.devicePixelRatio || 1;
            physicsCanvas.width = Math.floor(rect.width * dpr);
            physicsCanvas.height = Math.floor(rect.height * dpr);
            physicsCanvas.style.width = `${rect.width}px`;
            physicsCanvas.style.height = `${rect.height}px`;
            const ctx = physicsCanvas.getContext('2d');
            ctx.setTransform(dpr, 0, 0, dpr, 0, 0);
        }

        function createParticles() {
            const rect = physicsContainer.getBoundingClientRect();
            const width = physicsCanvas.width;
            const height = physicsCanvas.height;
            const particleCount = Math.floor((width * height) / 12000);

            particles = [];
            for (let i = 0; i < particleCount; i++) {
                particles.push(new Particle(
                    Math.random() * width,
                    Math.random() * height
                ));
            }
        }

        function animatePhysics(timestamp) {
            if (!particlesEnabled) return;

            // Calculate FPS with smoothing
            if (lastTime) {
                fps = 0.9 * fps + 0.1 * (1000 / (timestamp - lastTime));
            }
            lastTime = timestamp;

            const ctx = physicsCanvas.getContext('2d');
            const width = physicsCanvas.width;
            const height = physicsCanvas.height;

            // Clear with enhanced fade effect and motion blur
            ctx.fillStyle = 'rgba(2, 6, 10, 0.08)';
            ctx.fillRect(0, 0, width, height);

            // Update and draw particles with enhanced trails
            particles.forEach(particle => {
                particle.update(width, height);
                particle.draw(ctx);
            });

            // Draw enhanced mouse interaction area with pulsing effect
            const time = Date.now() * 0.002;
            const pulse = 1 + Math.sin(time) * 0.2;
            const effectiveRadius = mouse.radius * pulse;

            ctx.beginPath();
            ctx.arc(mouse.x, mouse.y, effectiveRadius, 0, Math.PI * 2);
            const gradient = ctx.createRadialGradient(mouse.x, mouse.y, 0, mouse.x, mouse.y, effectiveRadius);
            if (attractionMode) {
                gradient.addColorStop(0, 'rgba(0, 229, 255, 0.8)');
                gradient.addColorStop(0.5, 'rgba(0, 229, 255, 0.3)');
                gradient.addColorStop(1, 'rgba(0, 229, 255, 0.0)');
            } else {
                gradient.addColorStop(0, 'rgba(255, 0, 200, 0.8)');
                gradient.addColorStop(0.5, 'rgba(255, 0, 200, 0.3)');
                gradient.addColorStop(1, 'rgba(255, 0, 200, 0.0)');
            }
            ctx.fillStyle = gradient;
            ctx.fill();

            // Draw pulsing ring
            ctx.beginPath();
            ctx.arc(mouse.x, mouse.y, effectiveRadius, 0, Math.PI * 2);
            ctx.strokeStyle = attractionMode ? 'rgba(0, 229, 255, 0.9)' : 'rgba(255, 0, 200, 0.9)';
            ctx.lineWidth = 3;
            ctx.stroke();

            // Draw mouse pointer
            ctx.beginPath();
            ctx.arc(mouse.x, mouse.y, 4, 0, Math.PI * 2);
            ctx.fillStyle = attractionMode ? 'rgba(0, 229, 255, 1)' : 'rgba(255, 0, 200, 1)';
            ctx.fill();

            // Update status with smooth animation
            physicsStatus.textContent = `Particles: ${particles.length} | FPS: ${Math.round(fps)}`;

            requestAnimationFrame(animatePhysics);
        }

        // Gallery 3: Sacred Geometry
        const geometrySVG = document.getElementById('geometrySVG');
        const geometryContainer = document.getElementById('geometryContainer');
        const geometryStatus = document.getElementById('geometryStatus');
        const toggleRotationBtn = document.getElementById('toggleRotation');
        const togglePulseBtn = document.getElementById('togglePulse');
        const changePatternBtn = document.getElementById('changePattern');

        let rotationEnabled = true;
        let pulseEnabled = true;
        let currentPattern = 'flower';
        let rotationAngle = 0;
        let pulseFactor = 1;

        function resizeGeometry() {
            const rect = geometryContainer.getBoundingClientRect();
            geometrySVG.setAttribute('viewBox', `0 0 ${rect.width} ${rect.height}`);

            // Add SVG filters for enhanced visual effects
            if (!document.getElementById('geometrySVG').querySelector('defs')) {
                const defs = document.createElementNS('http://www.w3.org/2000/svg', 'defs');

                // Glow filter
                const filter = document.createElementNS('http://www.w3.org/2000/svg', 'filter');
                filter.setAttribute('id', 'glow');
                filter.setAttribute('x', '-50%');
                filter.setAttribute('y', '-50%');
                filter.setAttribute('width', '200%');
                filter.setAttribute('height', '200%');

                const feGaussianBlur = document.createElementNS('http://www.w3.org/2000/svg', 'feGaussianBlur');
                feGaussianBlur.setAttribute('stdDeviation', '2.5');
                feGaussianBlur.setAttribute('result', 'blur');

                const feMerge = document.createElementNS('http://www.w3.org/2000/svg', 'feMerge');
                const feMergeNode1 = document.createElementNS('http://www.w3.org/2000/svg', 'feMergeNode');
                const feMergeNode2 = document.createElementNS('http://www.w3.org/2000/svg', 'feMergeNode');
                feMergeNode2.setAttribute('in', 'SourceGraphic');

                feMerge.appendChild(feMergeNode1);
                feMerge.appendChild(feMergeNode2);

                filter.appendChild(feGaussianBlur);
                filter.appendChild(feMerge);
                defs.appendChild(filter);

                geometrySVG.appendChild(defs);
            }
        }

        function clearGeometry() {
            while (geometrySVG.firstChild) {
                geometrySVG.removeChild(geometrySVG.firstChild);
            }
        }

        function createCircle(cx, cy, r, color, opacity = 0.7) {
            const circle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
            circle.setAttribute('cx', cx);
            circle.setAttribute('cy', cy);
            circle.setAttribute('r', r);
            circle.setAttribute('fill', 'none');
            circle.setAttribute('stroke', color);
            circle.setAttribute('stroke-width', 1.5);
            circle.setAttribute('stroke-opacity', opacity);
            circle.setAttribute('filter', 'url(#glow)');
            return circle;
        }

        function createLine(x1, y1, x2, y2, color, width = 1) {
            const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
            line.setAttribute('x1', x1);
            line.setAttribute('y1', y1);
            line.setAttribute('x2', x2);
            line.setAttribute('y2', y2);
            line.setAttribute('stroke', color);
            line.setAttribute('stroke-width', width);
            line.setAttribute('stroke-opacity', 0.8);
            line.setAttribute('filter', 'url(#glow)');
            return line;
        }

        function drawFlowerOfLife() {
            const rect = geometryContainer.getBoundingClientRect();
            const width = rect.width;
            const height = rect.height;
            const cx = width / 2;
            const cy = height / 2;
            const baseRadius = Math.min(width, height) * 0.09 * pulseFactor;

            // Create rotating group
            const group = document.createElementNS('http://www.w3.org/2000/svg', 'g');
            group.setAttribute('transform', `rotate(${rotationAngle}, ${cx}, ${cy})`);

            // Time-based colors with enhanced gradients
            const time = Date.now() * 0.001;
            const hue1 = (time * 25) % 360;
            const hue2 = (time * 25 + 120) % 360;
            const hue3 = (time * 25 + 240) % 360;

            // Draw central circle with enhanced glow
            const centralCircle = createCircle(cx, cy, baseRadius, `hsl(${hue1}, 70%, 60%)`);
            centralCircle.setAttribute('filter', 'url(#glow)');
            centralCircle.setAttribute('stroke-width', 2.5);
            group.appendChild(centralCircle);

            // Draw first ring of 6 circles
            for (let i = 0; i < 6; i++) {
                const angle = (i / 6) * Math.PI * 2;
                const x = cx + Math.cos(angle) * baseRadius;
                const y = cy + Math.sin(angle) * baseRadius;
                const color = i % 3 === 0 ? `hsl(${hue1}, 70%, 60%)` :
                    i % 3 === 1 ? `hsl(${hue2}, 70%, 60%)` :
                        `hsl(${hue3}, 70%, 60%)`;
                group.appendChild(createCircle(x, y, baseRadius, color));
            }

            // Draw second ring of 12 circles
            for (let i = 0; i < 12; i++) {
                const angle = (i / 12) * Math.PI * 2 + Math.PI / 12;
                const distance = baseRadius * Math.sqrt(3);
                const x = cx + Math.cos(angle) * distance;
                const y = cy + Math.sin(angle) * distance;
                const color = i % 4 === 0 ? `hsl(${hue1}, 70%, 60%)` :
                    i % 4 === 1 ? `hsl(${hue2}, 70%, 60%)` :
                        i % 4 === 2 ? `hsl(${hue3}, 70%, 60%)` :
                            `hsl(${(hue1 + hue2) / 2}, 70%, 60%)`;
                group.appendChild(createCircle(x, y, baseRadius, color, 0.6));
            }

            // Draw third ring of 18 circles for more complexity
            for (let i = 0; i < 18; i++) {
                const angle = (i / 18) * Math.PI * 2;
                const distance = baseRadius * 2;
                const x = cx + Math.cos(angle) * distance;
                const y = cy + Math.sin(angle) * distance;
                const color = `hsl(${(hue1 + hue2 + hue3) / 3}, 70%, 60%)`;
                group.appendChild(createCircle(x, y, baseRadius * 0.8, color, 0.4));
            }

            // Add connecting lines for enhanced structure
            for (let i = 0; i < 6; i++) {
                const angle1 = (i / 6) * Math.PI * 2;
                const angle2 = ((i + 1) / 6) * Math.PI * 2;
                const x1 = cx + Math.cos(angle1) * baseRadius;
                const y1 = cy + Math.sin(angle1) * baseRadius;
                const x2 = cx + Math.cos(angle2) * baseRadius;
                const y2 = cy + Math.sin(angle2) * baseRadius;

                const line = createLine(x1, y1, x2, y2, `hsl(${(hue1 + hue2 + hue3) / 3}, 70%, 60%)`, 1);
                line.setAttribute('stroke-opacity', 0.3);
                group.appendChild(line);
            }

            geometrySVG.appendChild(group);
        }

        function drawMetatronsCube() {
            const rect = geometryContainer.getBoundingClientRect();
            const width = rect.width;
            const height = rect.height;
            const cx = width / 2;
            const cy = height / 2;
            const radius = Math.min(width, height) * 0.28 * pulseFactor;

            // Create rotating group
            const group = document.createElementNS('http://www.w3.org/2000/svg', 'g');
            group.setAttribute('transform', `rotate(${rotationAngle}, ${cx}, ${cy})`);

            // Time-based colors with enhanced palette
            const time = Date.now() * 0.001;
            const hue1 = (time * 15) % 360;
            const hue2 = (time * 15 + 180) % 360;
            const hue3 = (time * 15 + 90) % 360;

            // Create vertices of complex geometric pattern
            const vertices = [];

            // Inner circle vertices (12 points)
            for (let i = 0; i < 12; i++) {
                const angle = (i / 12) * Math.PI * 2;
                const r = radius * 0.6;
                const x = cx + Math.cos(angle) * r;
                const y = cy + Math.sin(angle) * r;
                vertices.push({ x, y });
            }

            // Outer circle vertices (12 points)
            for (let i = 0; i < 12; i++) {
                const angle = (i / 12) * Math.PI * 2 + Math.PI / 12;
                const r = radius;
                const x = cx + Math.cos(angle) * r;
                const y = cy + Math.sin(angle) * r;
                vertices.push({ x, y });
            }

            // Draw enhanced radial lines from center with varying colors
            for (let i = 0; i < 12; i++) {
                const color = i % 3 === 0 ? `hsl(${hue1}, 70%, 60%)` :
                    i % 3 === 1 ? `hsl(${hue2}, 70%, 60%)` :
                        `hsl(${hue3}, 70%, 60%)`;
                const line = createLine(cx, cy, vertices[i].x, vertices[i].y, color, 1.5);
                line.setAttribute('stroke-opacity', 0.8);
                group.appendChild(line);
            }

            // Draw inner connections with enhanced patterns
            for (let i = 0; i < 12; i++) {
                for (let j = i + 1; j < 12; j++) {
                    if ((j - i) % 3 === 0) {
                        const color = `hsl(${(hue1 + hue2) / 2}, 70%, 60%)`;
                        const line = createLine(vertices[i].x, vertices[i].y, vertices[j].x, vertices[j].y, color, 1);
                        line.setAttribute('stroke-opacity', 0.6);
                        group.appendChild(line);
                    }
                }
            }

            // Draw enhanced outer connections
            for (let i = 12; i < 24; i++) {
                for (let j = i + 1; j < 24; j++) {
                    if ((j - i) % 4 === 0) {
                        const color = `hsl(${(hue2 + hue3) / 2}, 70%, 60%)`;
                        const line = createLine(vertices[i].x, vertices[i].y, vertices[j].x, vertices[j].y, color, 1);
                        line.setAttribute('stroke-opacity', 0.5);
                        group.appendChild(line);
                    }
                }
            }

            // Draw enhanced cross connections between inner and outer
            for (let i = 0; i < 12; i++) {
                const j = (i + 6) % 12 + 12; // Connect to opposite outer vertex
                const color = `hsl(${(hue1 + hue3) / 2}, 70%, 60%)`;
                const line = createLine(vertices[i].x, vertices[i].y, vertices[j].x, vertices[j].y, color, 1.2);
                line.setAttribute('stroke-opacity', 0.7);
                group.appendChild(line);
            }

            // Add additional geometric patterns
            for (let i = 0; i < 12; i++) {
                const next = (i + 1) % 12;
                const color = `hsl(${(hue1 + hue2 + hue3) / 3}, 70%, 60%)`;
                const line = createLine(vertices[i].x, vertices[i].y, vertices[next].x, vertices[next].y, color, 0.8);
                line.setAttribute('stroke-opacity', 0.4);
                group.appendChild(line);
            }

            geometrySVG.appendChild(group);
        }

        function animateGeometry() {
            clearGeometry();

            if (currentPattern === 'flower') {
                drawFlowerOfLife();
                geometryStatus.textContent = 'Pattern: Flower of Life';
            } else {
                drawMetatronsCube();
                geometryStatus.textContent = 'Pattern: Metatron\'s Cube';
            }

            // Update animation parameters with enhanced effects
            if (rotationEnabled) {
                rotationAngle += 0.35;
            }

            if (pulseEnabled) {
                pulseFactor = 0.96 + Math.sin(Date.now() * 0.004) * 0.04;
            }
        }

        // Event Listeners
        window.addEventListener('resize', () => {
            resizeFractal();
            resizePhysics();
            resizeGeometry();
        });

        // Fractal controls
        toggleWindBtn.addEventListener('click', () => {
            windEnabled = !windEnabled;
            toggleWindBtn.textContent = windEnabled ? 'Pause Wind' : 'Resume Wind';
        });

        toggleGrowthBtn.addEventListener('click', () => {
            growthEnabled = !growthEnabled;
            toggleGrowthBtn.textContent = growthEnabled ? 'Pause Growth' : 'Resume Growth';
        });

        regenerateBtn.addEventListener('click', () => {
            fractalDepth = Math.floor(Math.random() * 4) + 8;
            animateFractal();
        });

        // Physics controls
        toggleAttractionBtn.addEventListener('click', () => {
            attractionMode = !attractionMode;
            toggleAttractionBtn.textContent = attractionMode ? 'Repulsion Mode' : 'Attraction Mode';
        });

        toggleParticlesBtn.addEventListener('click', () => {
            particlesEnabled = !particlesEnabled;
            toggleParticlesBtn.textContent = particlesEnabled ? 'Pause Particles' : 'Resume Particles';
            if (particlesEnabled) {
                requestAnimationFrame(animatePhysics);
            }
        });

        resetParticlesBtn.addEventListener('click', () => {
            createParticles();
        });

        physicsCanvas.addEventListener('mousemove', (e) => {
            const rect = physicsCanvas.getBoundingClientRect();
            mouse.x = e.clientX - rect.left;
            mouse.y = e.clientY - rect.top;
        });

        physicsCanvas.addEventListener('mouseleave', () => {
            mouse.x = -1000;
            mouse.y = -1000;
        });

        // Geometry controls
        toggleRotationBtn.addEventListener('click', () => {
            rotationEnabled = !rotationEnabled;
            toggleRotationBtn.textContent = rotationEnabled ? 'Pause Rotation' : 'Resume Rotation';
        });

        togglePulseBtn.addEventListener('click', () => {
            pulseEnabled = !pulseEnabled;
            togglePulseBtn.textContent = pulseEnabled ? 'Pause Pulse' : 'Resume Pulse';
        });

        changePatternBtn.addEventListener('click', () => {
            currentPattern = currentPattern === 'flower' ? 'cube' : 'flower';
        });

        // Enhanced initialization with better performance
        resizeFractal();
        resizePhysics();
        createParticles();
        resizeGeometry();

        // Start animations with optimized timing
        function startAnimations() {
            // Fractal animation - smooth 30 FPS
            setInterval(() => {
                if (document.getElementById('fractals').classList.contains('active')) {
                    animateFractal();
                }
            }, 33);

            // Physics animation - 60 FPS with requestAnimationFrame
            requestAnimationFrame(animatePhysics);

            // Geometry animation - smooth 30 FPS
            setInterval(() => {
                if (document.getElementById('geometry').classList.contains('active')) {
                    animateGeometry();
                }
            }, 33);
        }

        // Add page visibility optimization
        document.addEventListener('visibilitychange', () => {
            if (document.hidden) {
                // Pause animations when tab is not visible
                particlesEnabled = false;
            } else {
                // Resume animations when tab becomes visible
                particlesEnabled = true;
                requestAnimationFrame(animatePhysics);
            }
        });

        // Add keyboard shortcuts for enhanced user experience
        document.addEventListener('keydown', (e) => {
            // Spacebar: Pause/Resume all animations
            if (e.code === 'Space') {
                e.preventDefault();
                particlesEnabled = !particlesEnabled;
                toggleParticlesBtn.textContent = particlesEnabled ? 'Pause Particles' : 'Resume Particles';
                if (particlesEnabled) {
                    requestAnimationFrame(animatePhysics);
                }
            }

            // 1, 2, 3: Switch galleries
            if (e.code === 'Digit1') {
                document.querySelector('[data-page="fractals"]').click();
            } else if (e.code === 'Digit2') {
                document.querySelector('[data-page="physics"]').click();
            } else if (e.code === 'Digit3') {
                document.querySelector('[data-page="geometry"]').click();
            }

            // W: Toggle wind
            if (e.code === 'KeyW') {
                windEnabled = !windEnabled;
                toggleWindBtn.textContent = windEnabled ? 'Pause Wind' : 'Resume Wind';
            }

            // G: Toggle growth
            if (e.code === 'KeyG') {
                growthEnabled = !growthEnabled;
                toggleGrowthBtn.textContent = growthEnabled ? 'Pause Growth' : 'Resume Growth';
            }

            // R: Regenerate fractal
            if (e.code === 'KeyR') {
                fractalDepth = Math.floor(Math.random() * 4) + 8;
                animateFractal();
            }
        });

        startAnimations();
    </script>
</body>

</html>
